import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { PRODUCTS } from "../data/products";
import RelatedProducts from "./RelatedProducts";

function ProductDetails() {
  const { id } = useParams();
  const productId = parseInt(id, 10);
  const product = PRODUCTS.find((p) => p.id === productId);

  const [size, setSize] = useState("Small");
  const [quantity, setQuantity] = useState(1);

  if (!product) return <p className="text-center mt-20">Product not found.</p>;

  // Base price
  let price = product.price;
  if (size === "Medium") price += 0.5;
  if (size === "Large") price += 1; // Example: large adds $1

  return (
    <div className="px-10 md:px-36 py-16 bg-[#faf7f2] min-h-screen">
      <div className="flex flex-col md:flex-row gap-10">
        <img
          src={product.image}
          alt={product.name}
          className="w-full md:w-1/2 h-96 object-cover rounded-lg"
        />

        <div className="flex-1">
          <h2 className="text-4xl font-semibold mb-4 text-[#774b31]">
            {product.name}
          </h2>
          <p className="text-gray-700 mb-4">{product.description}</p>
          <p className="text-[#774b31] font-semibold text-2xl mb-4">
            ${(price * quantity).toFixed(2)}
          </p>

          {/* Size & Quantity buttons */}
          <div className="flex items-center gap-4 mb-6">
            <div className="flex gap-2">
              {["Small", "Medium", "Large"].map((s) => (
                <button
                  key={s}
                  onClick={() => setSize(s)}
                  className={`px-4 py-2 rounded ${
                    size === s
                      ? "bg-[#774b31] text-white"
                      : "bg-white border text-gray-700"
                  }`}
                >
                  {s}
                </button>
              ))}
            </div>

            <div className="flex items-center gap-2">
              <label>Qty:</label>
              <input
                type="number"
                min="1"
                value={quantity}
                onChange={(e) => setQuantity(parseInt(e.target.value))}
                className="border rounded px-2 py-1 w-16"
              />
            </div>
          </div>

          <button className="bg-[#774b31] text-white px-6 py-2 rounded hover:bg-[#633628] transition">
            Add to Cart
          </button>
        </div>
      </div>

      <RelatedProducts currentId={product.id} />
    </div>
  );
}

export default ProductDetails;
