import React, { useState } from "react";
import { useParams } from "react-router-dom";
import RelatedProducts from "./RelatedProducts";
import PRODUCTS from "../components/ProductGrid";

function ProductDetails() {
  const { id } = useParams(); // Get product ID from URL
  const product = PRODUCTS.find((p) => p.id === parseInt(id));

  const [size, setSize] = useState("Medium");
  const [quantity, setQuantity] = useState(1);

  if (!product)
    return <div className="text-center p-10">Product not found!</div>;

  return (
    <div className="px-10 md:px-36 py-16 bg-[#faf7f2] min-h-screen">
      <div className="flex flex-col md:flex-row gap-10">
        {/* Product Image */}
        <img
          src={product.image}
          alt={product.name}
          className="w-full md:w-1/2 h-auto rounded-lg shadow-lg"
        />

        {/* Product Info */}
        <div className="md:w-1/2 flex flex-col gap-4">
          <h2 className="text-4xl font-semibold text-[#774b31]">
            {product.name}
          </h2>
          <p className="text-gray-700">{product.description}</p>
          <span className="text-2xl font-bold">
            ${product.price.toFixed(2)}
          </span>

          {/* Size Selector */}
          <div className="flex items-center gap-4 mt-4">
            <span className="font-semibold">Size:</span>
            {["Small", "Medium", "Large"].map((s) => (
              <button
                key={s}
                onClick={() => setSize(s)}
                className={`px-4 py-2 rounded border ${
                  size === s ? "bg-[#774b31] text-white" : "bg-white"
                }`}
              >
                {s}
              </button>
            ))}
          </div>

          {/* Quantity Selector */}
          <div className="flex items-center gap-4 mt-4">
            <span className="font-semibold">Quantity:</span>
            <button
              onClick={() => setQuantity(quantity > 1 ? quantity - 1 : 1)}
              className="px-3 py-1 border rounded"
            >
              -
            </button>
            <span>{quantity}</span>
            <button
              onClick={() => setQuantity(quantity + 1)}
              className="px-3 py-1 border rounded"
            >
              +
            </button>
          </div>

          {/* Add to Cart */}
          <button className="mt-6 bg-[#774b31] text-white px-6 py-3 rounded hover:bg-[#633628] transition">
            Add to Cart
          </button>
        </div>
      </div>

      {/* Related Products */}
      <RelatedProducts currentId={product.id} />
    </div>
  );
}

export default ProductDetails;
