import React from "react";
import { Link } from "react-router-dom";
import Navbar from "./Navbar";
import axios from "axios";

function ProductCard({ product }) {
  return (
    <div className="bg-white rounded-xl shadow-lg p-4 text-center">
      <img
        src={product.image}
        alt={product.name}
        className="h-48 w-full object-cover rounded-lg mb-4"
      />
      <h3 className="text-xl font-semibold mb-2">{product.name}</h3>

      <p className="text-gray-700 mb-2">Description: {product.description}</p>

      <p className="text-gray-700 mb-1">Category: {product.category}</p>
      <p className="text-gray-700 mb-1">Size: {product.size}</p>

      <p className="text-gray-700 mb-2">Quantity: {product.quantity}</p>
      <p className="text-[#774b31] font-semibold mb-4">
        ${product.price ? product.price.toFixed(2) : "N/A"}
      </p>

      <div className="flex justify-center gap-2">
        <button className="bg-[#774b31] text-white px-2 py-2  rounded hover:bg-[#633628] transition">
          Add to Cart
        </button>
        <Link
          to={`/product/${product.item_id}`}
          className="bg-gray-200 text-[#774b31] px-2 py-2 rounded hover:bg-gray-300 transition"
        >
          View Details
        </Link>
      </div>
    </div>
  );
}

export default ProductCard;
